	/*学习java中的变量和变量类型
		 *byte（数字，1字节）
		 *short（数字，2字节）
		 *int （数字，4字节）
		 *long （数字，8字节）
		 *float（单精度数字，4字节）
		 *double（双精度数字，8字节）
		 *char（一个字母，2字节）
		 *boolean（true or false，1字节）
		 */
package lessons;

public class MyClassVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int my_byte=10;
		short my_short_number=20;
		int x=300067;
		long y=2345678;
		float my_fload_number=(float)4.5;
		double my_PI=3.1415926123;
		char my_char='A';
		boolean is_true=false;
		
		System.out.println(my_byte);
		System.out.println(my_short_number);
		System.out.println(x);
		System.out.println(y);
		System.out.println(my_fload_number);
		System.out.println(my_PI);
		System.out.println(my_char);
		System.out.println(is_true);
		
	}

}
